mapper.select:
select

from material
where customerCode = #{customerCode}
  and projectCode = #{projectCode}
  and materialObsName = #{materialObsName}
  and materialObsPath = #{materialObsPath}
  and activity = true
  and enable = true

select

from material
where customerCode = #{customerCode}
and projectCode = #{projectCode}
and mediaCode = #{mediaCode}
and activity = true
and enable = true
