mapper.update:
update user_pro_history
set addTime = now()
where miniOpenId = #{miniOpenId}
  and projectCode = #{projectCode}

mapper.select:
select

from user_pro_history
where activity = true
and enable = true

    and miniOpenId = #{miniOpenId}


    and userCode = #{userCode}

select projectCode
from user_pro_history
where miniOpenId = #{miniOpenId}
  and projectCode = #{projectCode}
  and activity = true
  and enable = true
